#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="F:\Develop\www\help-desk\node_modules\.pnpm\@biomejs+biome@2.2.6\node_modules\@biomejs\biome\bin\node_modules;F:\Develop\www\help-desk\node_modules\.pnpm\@biomejs+biome@2.2.6\node_modules\@biomejs\biome\node_modules;F:\Develop\www\help-desk\node_modules\.pnpm\@biomejs+biome@2.2.6\node_modules\@biomejs\node_modules;F:\Develop\www\help-desk\node_modules\.pnpm\@biomejs+biome@2.2.6\node_modules;F:\Develop\www\help-desk\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/f/Develop/www/help-desk/node_modules/.pnpm/@biomejs+biome@2.2.6/node_modules/@biomejs/biome/bin/node_modules:/mnt/f/Develop/www/help-desk/node_modules/.pnpm/@biomejs+biome@2.2.6/node_modules/@biomejs/biome/node_modules:/mnt/f/Develop/www/help-desk/node_modules/.pnpm/@biomejs+biome@2.2.6/node_modules/@biomejs/node_modules:/mnt/f/Develop/www/help-desk/node_modules/.pnpm/@biomejs+biome@2.2.6/node_modules:/mnt/f/Develop/www/help-desk/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../@biomejs/biome/bin/biome" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../@biomejs/biome/bin/biome" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../@biomejs/biome/bin/biome" $args
  } else {
    & "node$exe"  "$basedir/../@biomejs/biome/bin/biome" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
